mapper.select:
SELECT P.PL_NO , P.MEM_ID , P.PL_RDATE , NVL(p.pl_title, '제목 없음') as pl_title , P.PL_MSIZE , P.PL_PRIVATE , P.PL_THEME , P.PL_THUMBURL , COUNT(DISTINCT PLI.mem_id) AS LIKE_COUNT FROM PLANER P JOIN S_PLANER SP ON P.PL_NO = SP.PL_NO JOIN TOURITEMS T ON SP.CONTENT_ID = T.CONTENT_ID JOIN AREA_TB A ON T.AREA_CODE = A.AREA_CODE LEFT JOIN planer_like PLI ON P.pl_no = PLI.pl_no WHERE P.pl_private = 'Y' AND P.PL_NO IN ( SELECT SP.PL_NO FROM S_PLANER SP JOIN TOURITEMS T ON SP.CONTENT_ID = T.CONTENT_ID GROUP BY SP.PL_NO HAVING COUNT(DISTINCT T.AREA_CODE) = 1 )
AND A.AREA_CODE = #{areaCode}
GROUP BY P.PL_NO, P.MEM_ID, P.PL_RDATE, P.PL_TITLE, P.PL_MSIZE, P.PL_PRIVATE, P.PL_THEME, P.PL_THUMBURL

mapper.insert:
INSERT INTO planer_like (
   pl_like_no
   , mem_id
   , pl_like_date
   , pl_no
) VALUES (
   SEQ_PLANER_LIKE.NEXTVAL
   , #{memId}
   , sysdate
   , #{plNo}
)

mapper.delete:
DELETE FROM planer_like
WHERE mem_id = #{memId}
AND pl_no = #{plNo}
